function fv_flowplayer_admin_message_parse_group(aInfo) {
  var sOutput = '';
  if( typeof(aInfo) != "undefined" && Object.keys(aInfo).length > 0 ) {
    for( var j in aInfo ) {
      if( j == parseInt(j) ){
        sOutput += aInfo[j]+'<br />';
      } else if( typeof(aInfo[j]) == "function" ) {
        continue;
      } else {
        sOutput += j+': <tt>'+aInfo[j]+'</tt><br />';
      }
    }
  }
  if( sOutput.length > 0 ){
    sOutput = '<p>'+sOutput+'</p>';
  }
  return sOutput;
}



