    root.addClass('is-youtube');
    if( typeof fv_flowplayer_conf.youtube_browser_chrome != 'undefined' && fv_flowplayer_conf.youtube_browser_chrome == 'none' ) {
      root.addClass('is-youtube-nl');
    }
